import { useState } from "react";
import Sidebar from "../components/Sidebar/Sidebar";
import Topbar from "../components/Topbar/Topbar";

export default function MainLayout({ children }) {
  const [showBillModal, setShowBillModal] = useState(false);

  /* ---------------- SERVICES ---------------- */
  const [services, setServices] = useState([
    { service: "", staff: "", qty: "", price: "", gst: "" },
  ]);

  /* ---------------- PRODUCTS ---------------- */
  const [products, setProducts] = useState([
    { product: "", staff: "", qty: "", price: "", gst: "" },
  ]);

  /* ---------------- DISCOUNT ---------------- */
  const [applyDiscount, setApplyDiscount] = useState(false);
  const [discountType, setDiscountType] = useState("flat"); // flat | percent
  const [discountValue, setDiscountValue] = useState("");

  /* ================= SERVICE LOGIC ================= */

  const addServiceRow = () =>
    setServices([...services, { service: "", staff: "", qty: "", price: "", gst: "" }]);

  const removeServiceRow = (index) =>
    setServices(services.filter((_, i) => i !== index));

  const updateService = (index, field, value) => {
    const updated = [...services];
    updated[index][field] = value;
    setServices(updated);
  };

  const calculateServiceRowTotal = (row) => {
    const qty = Number(row.qty) || 0;
    const price = Number(row.price) || 0;
    const gst = Number(row.gst) || 0;

    const base = qty * price;
    return base + (base * gst) / 100;
  };

  /* ================= PRODUCT LOGIC ================= */

  const addProductRow = () =>
    setProducts([...products, { product: "", staff: "", qty: "", price: "", gst: "" }]);

  const removeProductRow = (index) =>
    setProducts(products.filter((_, i) => i !== index));

  const updateProduct = (index, field, value) => {
    const updated = [...products];
    updated[index][field] = value;
    setProducts(updated);
  };

  const calculateProductRowTotal = (row) => {
    const qty = Number(row.qty) || 0;
    const price = Number(row.price) || 0;
    const gst = Number(row.gst) || 0;

    const base = qty * price;
    return base + (base * gst) / 100;
  };

  /* ================= TOTALS ================= */

  const serviceTotal = services.reduce(
    (sum, row) => sum + calculateServiceRowTotal(row),
    0
  );

  const productTotal = products.reduce(
    (sum, row) => sum + calculateProductRowTotal(row),
    0
  );

  const subTotal = serviceTotal + productTotal;

  const discountAmount = (() => {
    const value = Number(discountValue) || 0;
    if (!applyDiscount) return 0;

    if (discountType === "percent") {
      if (value > 100) return 0;
      return (subTotal * value) / 100;
    }

    if (value > subTotal) return 0;
    return value;
  })();

  const payableAmount = subTotal - discountAmount;

  return (
    <div style={{ display: "flex" }}>
      <Sidebar />

      <div style={{ flex: 1 }}>
        <Topbar onAddBill={() => setShowBillModal(true)} />
        <main style={{ padding: "20px" }}>{children}</main>

        {showBillModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h3>Add Bill</h3>

              <input placeholder="Customer Name" />
              <input placeholder="Mobile Number" />

              {/* SERVICES */}
              <h4>Services</h4>
              {services.map((row, index) => (
                <div key={index} style={rowStyle}>
                  <select onChange={(e) => updateService(index, "service", e.target.value)}>
                    <option value="">Service</option>
                    <option>Haircut</option>
                    <option>Facial</option>
                    <option>Hair Spa</option>
                  </select>

                  <select onChange={(e) => updateService(index, "staff", e.target.value)}>
                    <option value="">Staff</option>
                    <option>Ramesh</option>
                    <option>Suresh</option>
                  </select>

                  <input type="number" placeholder="Qty" value={row.qty}
                    onChange={(e) => updateService(index, "qty", e.target.value)} />

                  <input type="number" placeholder="Price" value={row.price}
                    onChange={(e) => updateService(index, "price", e.target.value)} />

                  <input type="number" placeholder="GST %" value={row.gst}
                    onChange={(e) => updateService(index, "gst", e.target.value)} />

                  <strong>₹{calculateServiceRowTotal(row)}</strong>

                  {services.length > 1 && (
                    <button onClick={() => removeServiceRow(index)}>✕</button>
                  )}
                </div>
              ))}
              <button onClick={addServiceRow}>+ Add Service</button>

              {/* PRODUCTS */}
              <h4 style={{ marginTop: "16px" }}>Products</h4>
              {products.map((row, index) => (
                <div key={index} style={rowStyle}>
                  <select onChange={(e) => updateProduct(index, "product", e.target.value)}>
                    <option value="">Product</option>
                    <option>Shampoo</option>
                    <option>Hair Serum</option>
                    <option>Face Cream</option>
                  </select>

                  <select onChange={(e) => updateProduct(index, "staff", e.target.value)}>
                    <option value="">Staff</option>
                    <option>Ramesh</option>
                    <option>Suresh</option>
                  </select>

                  <input type="number" placeholder="Qty" value={row.qty}
                    onChange={(e) => updateProduct(index, "qty", e.target.value)} />

                  <input type="number" placeholder="Price" value={row.price}
                    onChange={(e) => updateProduct(index, "price", e.target.value)} />

                  <input type="number" placeholder="GST %" value={row.gst}
                    onChange={(e) => updateProduct(index, "gst", e.target.value)} />

                  <strong>₹{calculateProductRowTotal(row)}</strong>

                  {products.length > 1 && (
                    <button onClick={() => removeProductRow(index)}>✕</button>
                  )}
                </div>
              ))}
              <button onClick={addProductRow}>+ Add Product</button>

              {/* DISCOUNT */}
              <div style={{ marginTop: "20px", borderTop: "1px solid #ddd", paddingTop: "10px" }}>
                <label>
                  <input
                    type="checkbox"
                    checked={applyDiscount}
                    onChange={(e) => setApplyDiscount(e.target.checked)}
                  />{" "}
                  Apply Discount
                </label>

                {applyDiscount && (
                  <div style={{ display: "flex", gap: "10px", marginTop: "10px" }}>
                    <select onChange={(e) => setDiscountType(e.target.value)}>
                      <option value="flat">Flat ₹</option>
                      <option value="percent">Percentage %</option>
                    </select>

                    <input
                      type="number"
                      placeholder={discountType === "percent" ? "Discount %" : "Discount ₹"}
                      value={discountValue}
                      onChange={(e) => setDiscountValue(e.target.value)}
                    />
                  </div>
                )}
              </div>

              {/* TOTALS */}
              <h3 style={{ marginTop: "16px" }}>
                Subtotal: ₹{subTotal} <br />
                Discount: ₹{discountAmount} <br />
                <strong>Payable: ₹{payableAmount}</strong>
              </h3>

              <div className="modal-actions">
                <button onClick={() => setShowBillModal(false)}>Cancel</button>
                <button disabled={payableAmount <= 0}>Save Bill</button>
              </div>
            </div>
