import React from 'react';
import './Sidebar.css';

const Sidebar = ({ activeMenu, onMenuClick, onSignOut }) => {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'üìä' },
    { id: 'mystore', label: 'My Store', icon: 'üè™' },
    { id: 'inventory', label: 'Inventory', icon: 'üì¶' },
    { id: 'reports', label: 'Reports', icon: 'üìà' },
    { id: 'customers', label: 'Customers', icon: 'üë•' },
    { id: 'dailyexpense', label: 'Daily Expense', icon: 'üí∞' },
    { id: 'appointmenthistory', label: 'Appointment History', icon: 'üìÖ' },
    { id: 'myaccount', label: 'My Account', icon: '‚öôÔ∏è' }
  ];

  return (
    <div className="sidebar">
      <div className="sidebar-header">
        <h2>Salon Partner</h2>
      </div>
      <nav className="sidebar-nav">
        {menuItems.map((item) => (
          <div
            key={item.id}
            className={`sidebar-menu-item ${activeMenu === item.id ? 'active' : ''}`}
            onClick={() => onMenuClick(item.id)}
          >
            <span className="menu-icon">{item.icon}</span>
            <span className="menu-label">{item.label}</span>
          </div>
        ))}
        <div className="sidebar-menu-item signout" onClick={onSignOut}>
          <span className="menu-icon">üö™</span>
          <span className="menu-label">Sign Out</span>
        </div>
      </nav>
    </div>
  );
};

export default Sidebar;
