import { useState } from "react";
import Sidebar from "../components/Sidebar/Sidebar";
import Topbar from "../components/Topbar/Topbar";

export default function MainLayout({ children }) {
  const [showBillModal, setShowBillModal] = useState(false);

  const [services, setServices] = useState([
    { service: "", staff: "", qty: 0, price: 0, gst: 0 },
  ]);

  const [products, setProducts] = useState([
    { product: "", staff: "", qty: 0, price: 0, gst: 0 },
  ]);

  /* ---------------- SERVICES ---------------- */

  const addServiceRow = () => {
    setServices([
      ...services,
      { service: "", staff: "", qty: 0, price: 0, gst: 0 },
    ]);
  };

  const removeServiceRow = (index) => {
    setServices(services.filter((_, i) => i !== index));
  };

  const updateService = (index, field, value) => {
    const updated = [...services];
    updated[index][field] =
      field === "service" || field === "staff"
        ? value
        : Number(value) || 0;
    setServices(updated);
  };

  const calculateServiceRowTotal = (row) => {
    const base = row.qty * row.price;
    return base + (base * row.gst) / 100;
  };

  /* ---------------- PRODUCTS ---------------- */

  const addProductRow = () => {
    setProducts([
      ...products,
      { product: "", staff: "", qty: 0, price: 0, gst: 0 },
    ]);
  };

  const removeProductRow = (index) => {
    setProducts(products.filter((_, i) => i !== index));
  };

  const updateProduct = (index, field, value) => {
    const updated = [...products];
    updated[index][field] =
      field === "product" || field === "staff"
        ? value
        : Number(value) || 0;
    setProducts(updated);
  };

  const calculateProductRowTotal = (row) => {
    const base = row.qty * row.price;
    return base + (base * row.gst) / 100;
  };

  /* ---------------- TOTALS ---------------- */

  const serviceTotal = services.reduce(
    (sum, row) => sum + calculateServiceRowTotal(row),
    0
  );

  const productTotal = products.reduce(
    (sum, row) => sum + calculateProductRowTotal(row),
    0
  );

  const grandTotal = serviceTotal + productTotal;

  return (
    <div style={{ display: "flex" }}>
      <Sidebar />

      <div style={{ flex: 1 }}>
        <Topbar onAddBill={() => setShowBillModal(true)} />
        <main style={{ padding: "20px" }}>{children}</main>

        {showBillModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h3>Add Bill</h3>

              <input placeholder="Customer Name" />
              <input placeholder="Mobile Number" />

              {/* SERVICES */}
              <h4>Services</h4>

              {services.map((row, index) => (
                <div key={index} style={rowStyle}>
                  <select
                    value={row.service}
                    onChange={(e) =>
                      updateService(index, "service", e.target.value)
                    }
                  >
                    <option value="">Service</option>
                    <option>Haircut</option>
                    <option>Facial</option>
                    <option>Hair Spa</option>
                  </select>

                  <select
                    value={row.staff}
                    onChange={(e) =>
                      updateService(index, "staff", e.target.value)
                    }
                  >
                    <option value="">Staff</option>
                    <option>Ramesh</option>
                    <option>Suresh</option>
                  </select>

                  <input
                    type="number"
                    placeholder="Qty"
                    value={row.qty === 0 ? "" : row.qty}
                    onChange={(e) =>
                      updateService(index, "qty", e.target.value)
                    }
                  />

                  <input
                    type="number"
                    placeholder="Price"
                    value={row.price === 0 ? "" : row.price}
                    onChange={(e) =>
                      updateService(index, "price", e.target.value)
                    }
                  />

                  <input
                    type="number"
                    placeholder="GST %"
                    value={row.gst === 0 ? "" : row.gst}
                    onChange={(e) =>
                      updateService(index, "gst", e.target.value)
                    }
                  />

                  <strong>₹{calculateServiceRowTotal(row)}</strong>

                  {services.length > 1 && (
                    <button onClick={() => removeServiceRow(index)}>✕</button>
                  )}
                </div>
              ))}

              <button onClick={addServiceRow}>+ Add Service</button>

              {/* PRODUCTS */}
              <h4 style={{ marginTop: "16px" }}>Products</h4>

              {products.map((row, index) => (
                <div key={index} style={rowStyle}>
                  <select
                    value={row.product}
                    onChange={(e) =>
                      updateProduct(index, "product", e.target.value)
                    }
                  >
                    <option value="">Product</option>
                    <option>Shampoo</option>
                    <option>Hair Serum</option>
                    <option>Face Cream</option>
                  </select>

                  <select
                    value={row.staff}
                    onChange={(e) =>
                      updateProduct(index, "staff", e.target.value)
                    }
                  >
                    <option value="">Staff</option>
                    <option>Ramesh</option>
                    <option>Suresh</option>
                  </select>

                  <input
                    type="number"
                    placeholder="Qty"
                    value={row.qty === 0 ? "" : row.qty}
                    onChange={(e) =>
                      updateProduct(index, "qty", e.target.value)
                    }
                  />

                  <input
                    type="number"
                    placeholder="Price"
                    value={row.price === 0 ? "" : row.price}
                    onChange={(e) =>
                      updateProduct(index, "price", e.target.value)
                    }
                  />

                  <input
                    type="number"
                    placeholder="GST %"
                    value={row.gst === 0 ? "" : row.gst}
                    onChange={(e) =>
                      updateProduct(index, "gst", e.target.value)
                    }
                  />

                  <strong>₹{calculateProductRowTotal(row)}</strong>

                  {products.length > 1 && (
                    <button onClick={() => removeProductRow(index)}>✕</button>
                  )}
                </div>
              ))}

              <button onClick={addProductRow}>+ Add Product</button>

              {/* TOTALS */}
              <h3 style={{ marginTop: "16px" }}>
                Services: ₹{serviceTotal} <br />
                Products: ₹{productTotal} <br />
                Grand Total: ₹{grandTotal}
              </h3>

              <div className="modal-actions">
                <button onClick={() => setShowBillModal(false)}>
                  Cancel
                </button>
                <button>Save Bill</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

const rowStyle = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr 60px 80px 60px 80px auto",
  gap: "6px",
  marginBottom: "8px",
  alignItems: "center",
};
